import React,  { useState , Component  } from 'react';

import{Offcanvas
  ,OffcanvasHeader
  ,OffcanvasBody,Button
} from 'reactstrap';
class Off_canvas extends Component {

    constructor(props){
        super(props);
        this.state = { isopen: true };
        this.handleClick = this.updatestate.bind(this);
    }
    updatestate() {
        this.setState((state) => {
            // Important: read `state` instead of `this.state` when updating.
            return {isopen: !this.isopen}
          })
        
    }
    render() {
      return <div>
      <Button
        color="primary"
        onClick={function noRefCheck(){}}
      >
        Open
      </Button>
      <Offcanvas
        direction="end"
        scrollable
        isOpen ={this.state.isopen}
        toggle = {this.updatestate}
      >
        <OffcanvasHeader>
          Offcanvas
        </OffcanvasHeader>
        <OffcanvasBody>
          <strong>
            This is the Offcanvas body.
          </strong>
        </OffcanvasBody>
      </Offcanvas>
    </div>;
    }
  }






export {Off_canvas}
